function createStatement (payload) {
	const keys = Object.keys(payload).join(', ')
	const values = Object.values(payload).map(v => {
		if (Array.isArray(v)) {
			const res = v.map(i => `"${i}"`).join(',')
			return `'[${res}]'`
		}

		if (typeof v === 'string') {
			return `"${v}"`
		}
		return v
	}).join(', ')

	return `INSERT INTO block (${keys}) VALUES (${values})`
}

